using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEditor;namespace RogoDigital.Lipsync.AutoSync{	[CustomEditor(typeof(ASAzureSpeechRecognitionModule))]	public class ASAzureSpeechRecognitionModuleEditor : Editor	{		public override void OnInspectorGUI()		{			serializedObject.Update();			var languageCatChoice = serializedObject.FindProperty("langaugeCatChoice");			var languageChoice = serializedObject.FindProperty("languageChoice");			EditorGUI.BeginChangeCheck();			languageCatChoice.intValue = EditorGUILayout.Popup("Language", languageCatChoice.intValue, ASAzureSpeechRecognitionModule.languageCategoryNames);			if (EditorGUI.EndChangeCheck())			{				languageChoice.intValue = 0;			}			EditorGUI.BeginDisabledGroup(ASAzureSpeechRecognitionModule.languageDisplayNames[languageCatChoice.intValue].Length < 2);			languageChoice.intValue = EditorGUILayout.Popup("Variation", languageChoice.intValue, ASAzureSpeechRecognitionModule.languageDisplayNames[languageCatChoice.intValue]);			EditorGUI.EndDisabledGroup();			serializedObject.ApplyModifiedProperties();		}	}}